import { useEffect, useState } from "react";
import BackspaceIcon from "@mui/icons-material/Backspace";

function Box(props) {
  const [state, setState] = useState("text-black border-2 border-gray-300 rounded dark:bg-neutral-950 dark:text-white");

  useEffect(() => {
    setTimeout(() => {
      if (props.state === "C") setState("text-white bg-correct");
      if (props.state === "E") setState("text-white bg-exist");
      if (props.state === "N") setState("text-white bg-wrong dark:bg-gray-600");
    }, 125 * props.pos);
  }, [props.state]);

  return <div className={"grid w-12 h-12 p-0 m-0 text-2xl font-bold rounded-sm sm:w-14 sm:h-14 place-items-center" + state}>{props.value === "DEL" ? <BackspaceIcon /> : props.value}</div>;
}

export default Box;
