import Menu from "@mui/material/Menu";
import SettingsIcon from "@mui/icons-material/Settings";
import FormControlLabel from "@mui/material/FormControlLabel";
import { useState } from "react";
import { MenuItem, Switch } from "@mui/material";
import GitHubIcon from "@mui/icons-material/GitHub";

function Settings(props) {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleChange = () => {
    props.darkness(!props.dark);
  };

  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <SettingsIcon onClick={handleClick} className="text-black dark:text-white" />
      <Menu
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        slots={{
          style: { width: "100%" },
        }}
      >
        <FormControlLabel className=" text-slate-700" control={<Switch color="default" size="small" onChange={handleChange} />} label="Dark Mode" />
        <hr />
        <div className="flex justify-around pt-2">
          <a className="flex" href="https://github.com/JackScottow">
            <GitHubIcon />
          </a>
        </div>
      </Menu>
    </div>
  );
}

export default Settings;
