import { useEffect, useState } from "react";
import BackspaceIcon from "@mui/icons-material/Backspace";

function Box(props) {
  const [state, setState] = useState("text-black border-2 border-zinc-700 rounded dark:bg-neutral-900 dark:text-white");

  useEffect(() => {
    setTimeout(() => {
      if (props.state === "C")
        setTimeout(() => {
          setState("animate-bounce");
        }, 1000);
      setState("text-white bg-correct animate-bounce");
      if (props.state === "E") setState("text-white bg-exist animate-bounce");
      if (props.state === "N") setState("text-white bg-wrong dark:bg-gray-600 animate-bounce");
    }, 125 * props.pos);
  }, [props.state]);

  return <div className={"grid place-items-center w-12 h-12 p-0 m-0 text-4xl font-bold rounded-sm sm:w-14 sm:h-14 " + state}>{props.value === "DEL" ? <BackspaceIcon /> : props.value}</div>;
}

export default Box;
